try {

    //search for "Temperature" or "temperature"
    var foundStr = "";
    var hasTemp = msg.payload.indexOf("temperature");

    if(hasTemp < 0) {
        hasTemp = msg.payload.indexOf("Temperature");
    }

    if(hasTemp >= 0) {
        var s = -1;
        var e = -1;

        var i = 0;
        for(i = hasTemp; i >= 0; i--) {
            if(msg.payload[i] == "{") {
                s = i;
                break;
            }
        }

        for(i = hasTemp; i < msg.payload.length; i++) {
            if(msg.payload[i] == "}") {
                e = i + 1;
                break;
            }
        }

        if(s > -1 && e > -1) {
            foundStr = msg.payload.substring(s,e);
            //node.warn("foundStr = " + s + " /" + e + " => " + foundStr);
        }
    }

    // send temperature object
    if(foundStr.length > 0) {
        msg.payload = foundStr.replace("Temperature","temperature");
        return msg;
    }
} catch(err) {

}


