myconsolelog("MOPOP_CalcScore ... walls => " + JSON.stringify(walls));

var wallsScore = 0;
var usedYearPerWall = {};

var totalWeight = 1;
var totalGroup = 1;
var totalOrder = 1;

// go over walls
for (var wKey in walls) {
    myconsolelog("MOPOP_CalcScore ... wKey => " + wKey);
    if (walls.hasOwnProperty(wKey)) {
        usedYearPerWall[wKey] = {};
        myconsolelog("MOPOP_CalcScore ... wKey => " + wKey + " => FOUND");
        var aWall = walls[wKey];
        var wName = aWall.name;
        var wHooks = aWall.hooks;
        var isReversed = aWall.reversed;
        var wallW = aWall.w;
        var wallH = aWall.h;
        var wallGenre = aWall.genre;
        myconsolelog("MOPOP_CalcScore ... walls[" + wKey + "].name=" + wName + ", " + wallGenre);

        var occupantList = [];
        for (var hKey in wHooks) {
            myconsolelog("MOPOP_CalcScore ...   hKey => " + hKey);
            if (wHooks.hasOwnProperty(hKey)) {
                var aHook = wHooks[hKey];

                var wuInfoName = "WU_" + hKey;
                var wuInfo = MAIN.PUI.WorldUI.Find(wuInfoName);

                var wuScBoardName = flow.get("mopop_score_chart_board");
                var wuScBoard = MAIN.PUI.WorldUI.Find(wuScBoardName);

                // is hook occupied?
                var isOccupied = aHook.isOccupied;
                if (isOccupied) {

                    //prepare list of occupants
                    occupantList.push({
                        "hook": aHook,
                        "hookname": hKey
                    });

                    var occupante = aHook.occupante;
                    myconsolelog("MOPOP_CalcScore ... walls[" + wKey + "].hooks[" + hKey + "].occupied=" + isOccupied);
                    myconsolelog("MOPOP_CalcScore ... walls[" + wKey + "].hooks[" + hKey + "].occupante=>" + JSON.stringify(occupante));

                    // check if tehre is a stored occupant
                    var occupanteId = occupante.TrackableName;
                    if (MOPOP.artefacts.hasOwnProperty(occupanteId)) {

                        var wuInfoContent = "";

                        var artefact = MOPOP.artefacts[occupanteId];

                        var genre = artefact.genre;
                        var score = artefact.score;
                        var year = MOPOP_GetYearFromString(artefact.publishdate);
                        var extraWeight = 1;

                        //count as zero 
                        if(genre != wallGenre) {
                            extraWeight = 0;
                            wuInfoContent += "Wrong Genre!" + "</br>";
                            myconsolelog("MOPOP_CalcScore ... Artefact[" + occupanteId + "].genre => " + genre + " != " + wallGenre);
                        }

                        // #sold copies normalizer: 1x10^6 ==> 1
                        var soldcopies = 0;
                        if (artefact.hasOwnProperty("soldcopies")) {
                            soldcopies = parseFloat(artefact.soldcopies.replace(",", ""));
                            soldcopies *= 0.000001;
                        }


                        //year occupation for year distribution
                        var yearWeight = 1;
                        var yearOk = (parseFloat(year) >=  MOPOP.startyear) || (parseFloat(year) <=  MOPOP.endyear);
                        if(yearOk) {
                            if (!usedYearPerWall[wKey].hasOwnProperty(year)) {
                                var dataId = parseFloat(year) - parseFloat(MOPOP.startyear);
                                usedYearPerWall[wKey][year] = {
                                    "val": 0,
                                    "id": dataId
                                };
                                //usedYearPerWall[wKey][year].val += 1;
                                myconsolelog("MOPOP_CalcScore ... Artefact[" + occupanteId + "].score => " + score);
                            }
                        } else {
                            yearWeight = 0;
                        }



                        var soldScore = parseFloat(score) * soldcopies;
                        var contribScore = soldScore * extraWeight * yearWeight;

                        wuInfoContent += "Sold: " + soldScore.toFixed(2) + "</br>";
                        wuInfoContent += "Weight: " + extraWeight.toFixed(2) + "</br>";
                        wuInfoContent += "Single: " + contribScore.toFixed(2) + "</br>";

                        usedYearPerWall[wKey][year].val += 1;
                        wallsScore += contribScore;

                        if(wuInfo != null) {
                            wuInfo.document.innerHTML = 
                                '<div id="wrap" style="font-size:24px;background-color:silver;color:black;border:solid 4px blue;margin:5px;padding:5px;border-radius:4px;">' + 
                                wuInfoContent + 
                                "</div>";
                        }
                    }
                } else {
                    if(wuInfo != null) {
                        wuInfo.document.innerHTML = "";
                    }
                }
            }
        }

        // calc occupant features, artist groups, ... here
        try {
            if (occupantList.length > 0) { // was 2

                //reversed for year direction
                //isReversed
                var NBins = wallW;
                var xBins = [];

                //prepare xbins
                for (var wi = 0; wi < NBins; wi++) {
                    xBins.push([]);
                }

                var weightedPenalty = 1;
                var groupBonus = 1;
                var orderWeight=1;

                //collect years of occupants
                var N = occupantList.length;
                for (var i = 0; i < N; i++) {
                    var occ = occupantList[i];
                    var hook = occ.hook;
                    var hookname = occ.hookname;
                    var hookOccupant = hook.occupante;
                    occupanteId = hookOccupant.TrackableName;

                    var wuInfoName = "WU_" + hookname;
                    var wuInfo = MAIN.PUI.WorldUI.Find(wuInfoName);

                    myconsolelog("MOPOP_CalcScore OCCULIST => hookname= " + hookname);
                    myconsolelog("MOPOP_CalcScore OCCULIST => occ= " + JSON.stringify(occ));
                    myconsolelog("MOPOP_CalcScore OCCULIST => hookOccupant= " + JSON.stringify(hookOccupant));
                    myconsolelog("MOPOP_CalcScore OCCULIST => occupanteId= " + hookOccupant.TrackableName);
                    myconsolelog("MOPOP_CalcScore OCCULIST => occupanteId= " + occupanteId);


                    var hookX = hook.x;
                    var hookY = hook.y;

                    var hooksArtefact = MOPOP.artefacts[occupanteId];
                    var hooksArtist = hooksArtefact.artist;

                    myconsolelog("MOPOP_CalcScore OCCULIST => hooksArtist= " + hooksArtist);
                    myconsolelog("MOPOP_CalcScore OCCULIST => hookX= " + hookX + ", hookY=" + hookY);


                    var hooksYearStr = MOPOP_GetYearFromString(hooksArtefact.publishdate);
                    var hooksYear = parseInt(hooksYearStr);

                    //xBins[hookX].push(hooksYear); //was this
                    xBins[hookX].push({"year": hooksYear, "hookname": hookname, "occupanteId": occupanteId});

                    //var groupBonus = 1;

                    var partgroup = 1;
                    //look for other artists close by
                    // start at i = k +1  
                    if(true) {
                    //if(i + 1 < N) {
                        for(var ia = 0; ia < N; ia++ ) {
                        //for(var ia = i + 1; ia < N; ia++ ) {
                            var tocc = occupantList[ia];
                            var thook = tocc.hook;
                            var thookX = thook.x;
                            var thookY = thook.y;
                            var thookname = tocc.hookname;
                            var thookOccupant = thook.occupante;
                            var toccupanteId = thookOccupant.TrackableName;
                            var thooksArtefact = MOPOP.artefacts[toccupanteId];
                            var thooksArtist = thooksArtefact.artist;
                            myconsolelog("MOPOP_CalcScore OCCULIST => hookX= " + hookX + ", hookY=" + hookY);

                            myconsolelog("MOPOP_CalcScore artist => thooksArtist= " + thooksArtist + ", hooksArtist=" + hooksArtist);
                            // if the same artis
                            if(thooksArtist == hooksArtist && toccupanteId != occupanteId) {
                            //if(thooksArtist == hooksArtist) {
                                dhx = Math.abs(thookX - hookX);
                                dhy = Math.abs(thookY - hookY);
                                //check if its placed on a hook next to it
                                if(dhx == 1 || dhy == 1) {
                                //if(dhx < 2 || dhy < 2) {
                                    myconsolelog("MOPOP_CalcScore OCCULIST ARTISTBONUS");
                                    weightedPenalty *= 1.2;
                                    groupBonus *= 1.2;
                                    partgroup *= 1.2;
                                    wuInfo.document.getElementById("wrap").innerHTML += "Group: " + partgroup.toFixed(2) + "</br>";
                                }
                            }
                        }
                    }

                    //if(wuInfo != null) {
                        //wuInfo.document.getElementById("wrap").innerHTML += 
                        //"GroupAll: " + groupBonus.toFixed(2) + "</br>";
                        //wuInfo.document.innerHTML += "groupBonus: " + groupBonus.toFixed(2) + "</br>";
                    //}

                    if(wuScBoard != null) {
                        wuScBoard.document.getElementById("group").innerHTML = groupBonus.toFixed(2);
                    }
                }

                if(wuScBoard != null) {
                    wuScBoard.document.getElementById("order").innerHTML = orderWeight.toFixed(2);
                    wuScBoard.document.getElementById("penalty").innerHTML = weightedPenalty.toFixed(2);
                }

                //filter only used bins, so that we can go pairwise through them
                var filteredBins = [];
                for (var wi = 0; wi < NBins; wi++) {
                    if (xBins[wi].length > 0) {
                        filteredBins.push(xBins[wi]);
                    }
                }

                //console.log("MOPOP_CalcScore xBins => " + JSON.stringify(xBins));
                //console.log("MOPOP_CalcScore filteredBins => " + JSON.stringify(filteredBins));

                //check for weak monotonic years
                var fNbins = filteredBins.length;
                for (var wi = 1; wi < fNbins; wi++) {
                    var prevBin = filteredBins[wi - 1];
                    var curBin = filteredBins[wi];

                    for (pwi = 0; pwi < prevBin.length; pwi++) {
                        //curPrevYear = prevBin[pwi]; //was this
                        curPrevYear = prevBin[pwi].year; //was

                        var curPrevHookname = prevBin[pwi].hookname;
                        var curPrevWu = MAIN.PUI.WorldUI.Find("WU_" + curPrevHookname);
                        var tempWeight = 1;

                        for (cwi = 0; cwi < curBin.length; cwi++) {
                            //curCurYear = curBin[cwi]; //was this
                            curCurYear = curBin[cwi].year; //was this
                            var curCurHookname = prevBin[pwi].hookname;


                            //console.log("MOPOP_CalcScore years check => " + curPrevYear + " vs " + curCurYear);

                            //check each year of bin A vs each year of bin b
                            //every violation causes a 5% penalty
                            if (isReversed) {

                                //reverse means year gos from 1990 to 2000 where the hook ids go from 13 to 0
                                //therefore to be monotonic increasing, the years seen from the ids would be
                                // decreasing, theferefor we penalize when its increasing
                                if (curPrevYear < curCurYear) {
                                    weightedPenalty *= 0.8; // local order penalty
                                    tempWeight *= 0.8;
                                }
                            } else {
                                //regular monotonic increase where the yeare goes from 1990 to 2000 and the
                                // hook id from 0 to 13, therefore we penalize violations when decreasing
                                // which means prev year is bigger as next year
                                if (curPrevYear > curCurYear) {
                                    weightedPenalty *= 0.8;
                                    tempWeight *= 0.8; //local order penalty
                                    orderWeight *=0.8;
                                }
                            }
                        }
                    }
                }

                if(wuScBoard != null) {
                    totalWeight *= weightedPenalty;
                    totalGroup *= groupBonus;
                    totalOrder *= orderWeight;
                    wuScBoard.document.getElementById("order").innerHTML = totalOrder.toFixed(2);
                    wuScBoard.document.getElementById("penalty").innerHTML = totalWeight.toFixed(2);
                    wuScBoard.document.getElementById("group").innerHTML = totalGroup.toFixed(2);
                }

                wallsScore *= weightedPenalty;
            }
        } catch (tlerr) {
            console.log("MOPOP_CalcScore TL ERROR => " + tlerr);
        }
    }

} // walls

    if (wallsScore > 0) {
        //MOPOP_ReportUserEvent(MOPOP.userevents.score, { "score": wallsScore }, Date.now(), MOPOP.user);

