        //node.send([{"topic": "MOPOP/wallscore", "payload":jsonScores}]);
        node.send([{"topic": "MOPOP/wallscore", "payload": data}]);

        for (var k in usedYearPerWall) {
            var jsonYears = JSON.stringify(usedYearPerWall[k]);
            var topic = MOPOP.topics.wallyearhist + "/" + k.replace("HW_", ""); //dirty fix!!!!
            //RT.MQTT.Publish(topic, jsonYears);
            myconsolelog("MOPOP_CalcScore ... done YEARS: " + topic + "/ => " + jsonYears);

            var keys = Object.keys(usedYearPerWall[k]);
            myconsolelog("MOPOP_CalcScore ... done YEARS: " + topic + "/ => #" + keys.length);

            //node.send([{"topic": topic, "payload":jsonYears}]);
