var wallsScore = 0;
var usedYearPerWall = {};

for(var walli = 0; walli < walls.length; walli++) {
    var wall = walls[walli];
    var hw = hookwall[wall];
    var hwhooks = hw.hooks;
    var wallGenre = hw.genre;

    var keys = Object.keys(hwhooks);

    usedYearPerWall[wall] = {};
    var occupantList = [];
    for(var j = 0; j < keys.length; j++) {
        var k = keys[j];

        var isOccu = hwhooks[k].isOccupied;
        if(isOccu) {
            var occu = hwhooks[k].occupante;
            var recordid = occu.TrackableName;

            //prepare list of occupants
            occupantList.push({
                "hook": occu,
                "hookname": k
            });

            var artefact = records[recordid];
            var genre = artefact.genre;
            var score = artefact.score;
            const year = new Date(artefact.publishdate).getFullYear();
            var extraWeight = 1;
            //var wuInfoContent = "";

            if(genre != wallGenre) {
                extraWeight = 0;
                //wuInfoContent += "Wrong Genre!" + "</br>";
                //node.warn("MOPOP_CalcScore ... Artefact[" + recordid + "].genre => " + genre + " != " + wallGenre);
            }

            var soldcopies = 0;
            if (artefact.hasOwnProperty("soldcopies")) {
                soldcopies = parseFloat(artefact.soldcopies.replace(",", ""));
                soldcopies *= 0.000001;
            }

            var yearWeight = 1;
            var yearOk = (parseFloat(year) >=  hw.startyear) || (parseFloat(year) <=  hw.endyear);
            if(yearOk) {
                if (!usedYearPerWall[wall].hasOwnProperty(year)) {
                    var dataId = parseFloat(year) - parseFloat(hw.startyear);
                    usedYearPerWall[wall][year] = {
                        "val": 0,
                        "id": dataId
                    };
                    //usedYearPerWall[wKey][year].val += 1;
                }
            } else {
                yearWeight = 0;
            }

            var soldScore = parseFloat(score) * soldcopies;
            //node.warn("soldScore => " + soldScore);

            var contribScore = soldScore * extraWeight * yearWeight;
            //node.warn("contribScore => " + contribScore);

            usedYearPerWall[wall][year].val += 1;
            wallsScore += contribScore;

        } //isOccupied
    } //hooks

    //occupant list checks, neighborhood
    if (occupantList.length > 0) { // was 2

        var wallW = 6;

        //reversed for year direction
        //isReversed
        var isReversed = false;
        var NBins = wallW;
        var xBins = [];

        //prepare xbins
        for (var wi = 0; wi < NBins; wi++) {
            xBins.push([]);
        }

        var weightedPenalty = 1;
        var groupBonus = 1;

        //collect years of occupants
        var N = occupantList.length;
        for (var i = 0; i < N; i++) {
            var occ = occupantList[i];
            var hook = occ.hook;
            node.warn(hook);
            var hookname = occ.hookname;
            var hookOccupant = hook;//hook.occupante;
            occupanteId = hookOccupant.TrackableName;

            var hookparts = hookname.split("_");
            var hookX = hookparts[2];//hook.x;
            var hookY = hookparts[3];//hook.y;

            var hooksArtefact = records[occupanteId];
            var hooksArtist = hooksArtefact.artist;

            var hooksYearStr = new Date(hooksArtefact.publishdate).getFullYear();
            var hooksYear = parseInt(hooksYearStr);

            //node.warn("before push => " + xBins + " | " + hookX + " | occ => " + JSON.stringify(occ));
            xBins[hookX].push({"year": hooksYear, "hookname": hookname, "occupanteId": occupanteId});
            //node.warn("after push");

            //var groupBonus = 1;

            //look for other artists close by
            // start at i = k +1  
            if(true) {
            //if(i + 1 < N) {
                for(var ia = 0; ia < N; ia++ ) {
                //for(var ia = i + 1; ia < N; ia++ ) {
                    var tocc = occupantList[ia];
                    var thook = tocc.hook;
                    //var thookX = thook.x;
                    //var thookY = thook.y;
                    var thookparts = tocc.hookname.split("_");
                    var thookX = thookparts[2];//hook.x;
                    var thookY = thookparts[3];//hook.y;
                    var thookname = tocc.hookname;
                    var thookOccupant = thook;//thook.occupante;
                    var toccupanteId = thookOccupant.TrackableName;
                    var thooksArtefact = records[toccupanteId];
                    var thooksArtist = thooksArtefact.artist;

                    node.warn(thooksArtist + " vs " + hooksArtist + ", " + toccupanteId + " vs " + occupanteId);
                    // if the same artis
                    if(thooksArtist == hooksArtist && toccupanteId != occupanteId) {
                        dhx = Math.abs(thookX - hookX);
                        dhy = Math.abs(thookY - hookY);
                        //node.warn("thookX=" + thookX + ", hookX=" + hookX + ", dhx=" + dhx);
                        //node.warn("thookY=" + thookY + ", hookY=" + hookY + ", dhy=" + dhy);
                        //check if its placed on a hook next to it
                        if(dhx < 2 && dhy < 2) {
                            weightedPenalty *= 1.2;
                            groupBonus *= 1.2;
                        }
                    }
                }
            }
        }

        node.warn(xBins);


        //filter only used bins, so that we can go pairwise through them
        var filteredBins = [];
        for (var wi = 0; wi < NBins; wi++) {
            if (xBins[wi].length > 0) {
                filteredBins.push(xBins[wi]);
            }
        }

        var tempWeight = 1;
        //check for weak monotonic years
        var fNbins = filteredBins.length;
        for (var wi = 1; wi < fNbins; wi++) {

            var prevBin = filteredBins[wi - 1];
            var curBin = filteredBins[wi];

            for (pwi = 0; pwi < prevBin.length; pwi++) {
                //curPrevYear = prevBin[pwi]; //was this
                curPrevYear = prevBin[pwi].year; //was

                var curPrevHookname = prevBin[pwi].hookname;
                //var curPrevWu = MAIN.PUI.WorldUI.Find("WU_" + curPrevHookname);
                var curPrevWu = null;

                for (cwi = 0; cwi < curBin.length; cwi++) {
                    //curCurYear = curBin[cwi]; //was this
                    curCurYear = curBin[cwi].year; //was this
                    var curCurHookname = prevBin[pwi].hookname;

                    //check each year of bin A vs each year of bin b
                    //every violation causes a 5% penalty
                    if (isReversed) {

                        //reverse means year gos from 1990 to 2000 where the hook ids go from 13 to 0
                        //therefore to be monotonic increasing, the years seen from the ids would be
                        // decreasing, theferefor we penalize when its increasing
                        if (curPrevYear < curCurYear) {
                            weightedPenalty *= 0.8;
                            tempWeight *= 0.8;
                        }
                    } else {
                        //regular monotonic increase where the yeare goes from 1990 to 2000 and the
                        // hook id from 0 to 13, therefore we penalize violations when decreasing
                        // which means prev year is bigger as next year
                        if (curPrevYear > curCurYear) {
                            weightedPenalty *= 0.8;
                            tempWeight *= 0.8;
                        }
                    }
                }



                if(curPrevWu != null) {
                    curPrevWu.document.getElementById("wrap").innerHTML += 
                    "Order penalty: " + tempWeight.toFixed(2) + "</br>";
                }


                //TODO maybe check violations inside a xBin
                /*
                if (curYear != prevYear) {
                    weightedPenalty += -0.01;
                }
                nextYear = prevBin[cwi - 1];
                */
            }
        }
        //node.warn("wall=" + wall);
        //node.warn("weightedPenalty=" + weightedPenalty);
        //node.warn("tempWeight=" + tempWeight);
        //node.warn("groupBonus=" + groupBonus);

        var repmsg = {};
        repmsg.payload = {};
        repmsg.payload[wall] = {};
        repmsg.payload[wall].penalty = weightedPenalty.toFixed(2);
        repmsg.payload[wall].group = groupBonus.toFixed(2);
        repmsg.payload[wall].order = tempWeight.toFixed(2);
        node.warn(repmsg);
        if(wall == "popwall") {
            node.send([null, repmsg, null, null]);
        } else {
            node.send([null, null, repmsg, null]);
        }
        //node.send([null, null, null, repmsg]);
        wallsScore *= weightedPenalty;
    }
} //walls


