func:
msg.viewBuilder = {};
msg.viewBuilder.viewName = "RTDS Demo";
msg.viewBuilder.pointCount = 100;

msg.view = {};
msg.view.name = msg.viewBuilder.viewName;
msg.view.attributes = [];
msg.view.data = [];
let xData = [];
let yData = [];
let zData = [];
let colors = [];
for(let i = 0; i < msg.viewBuilder.pointCount; i++) {
    let temp = Math.random();
    temp = temp * temp;
    let act = Math.random();
    xData.push(temp);
    yData.push(act);
    zData.push(0);
    colors.push([temp, 0, act]);
}
msg.view.data[CNRIF.ViewData.XPosition] = xData;
msg.view.data[CNRIF.ViewData.YPosition] = yData;
msg.view.data[CNRIF.ViewData.ZPosition] = zData;
msg.view.data[CNRIF.ViewData.Color] = colors;
return msg;
