func:
msg.viewBuilder = {};
msg.viewBuilder.viewName = "RTDS Demo";
msg.viewBuilder.pointCount = 7;

msg.view = {};
msg.view.name = msg.viewBuilder.viewName;
msg.view.attributes = [];
msg.view.data = [];
let xData = [];
let yData = [];
let zData = [];
let colors = [];
for(let i = 0; i < msg.viewBuilder.pointCount; i++) {
    xData.push(1 / (msg.viewBuilder.pointCount - 1) * i);
    yData.push(0);
    zData.push(0);
    colors.push([1, 0, 0]);
}
msg.view.data[CNRIF.ViewData.XPosition] = xData;
msg.view.data[CNRIF.ViewData.YPosition] = yData;
msg.view.data[CNRIF.ViewData.ZPosition] = zData;
msg.view.data[CNRIF.ViewData.Color] = colors;
return msg;

CNRIF.RTDS.SetData(msg.rtds.name, "Weekday", "Monday", 1);
CNRIF.RTDS.SetData(msg.rtds.name, "Weekday", "Tuesday", 1);
CNRIF.RTDS.SetData(msg.rtds.name, "Weekday", "Wednesday", 1);
CNRIF.RTDS.SetData(msg.rtds.name, "Weekday", "Thursday", 1);
CNRIF.RTDS.SetData(msg.rtds.name, "Weekday", "Friday", 1);
CNRIF.RTDS.SetData(msg.rtds.name, "Weekday", "Saturday", 1);
CNRIF.RTDS.SetData(msg.rtds.name, "Weekday", "Sunday", 1);
return msg;
