func:
msg.viewBuilder = {};
msg.viewBuilder.pointCount = 20;

msg.view = {};
msg.view.name = msg.viewBuilder.viewName;
msg.view.attributes = [];
msg.view.data = [];
let xData = [];
let yData = [];
let zData = [];
let colors = [];
let linkData = [];
for(let i = 0; i < msg.viewBuilder.pointCount; i++) {
    let x = i / (msg.viewBuilder.pointCount - 1);
    let y = x * x;
    xData.push(x);
    yData.push(y);
    zData.push(0);
    colors.push([x,  1 - y, 1]);
    linkData.push(0);
}
msg.view.data[CNRIF.ViewData.XPosition] = xData;
msg.view.data[CNRIF.ViewData.YPosition] = yData;
msg.view.data[CNRIF.ViewData.ZPosition] = zData;
msg.view.data[CNRIF.ViewData.Color] = colors;
msg.view.data[CNRIF.ViewData.Indices] = {isLinking: true, data: linkData};
return msg;
